//000000000300a:21:{s:2:"id";s:3:"412";s:6:"userid";s:36:"6635A7B2_34A9_BEA5_7750_0BF9CDAA3185";s:8:"nickname";s:12:"长运驾校";s:3:"img";s:40:"6635A7B2_34A9_BEA5_7750_0BF9CDAA3185.jpg";s:8:"piccount";s:1:"1";s:5:"phone";s:12:"400-8040-517";s:7:"address";s:9:"留和路";s:9:"pickrange";s:0:"";s:5:"score";s:1:"0";s:12:"introduction";s:1570:"杭州长运驾驶培训学校(长运驾校)创办于1979年5月，是浙江省最早期的驾驶培训学校之一，专业从事汽车驾驶员和交通行业各类技术等级培训，属杭州市一类驾校。在历年的行业教学评估中，学校多次获得&#8220;优胜单位&#8221;和&#8220;优秀培训业户&#8221;等荣誉称号。迄今已为社会培养初、中、高级驾驶员、修理工等各类交通技术人才6万余名，得到了社会各界的广泛认同和良好评价。    长运驾校具体提供的各类培训服务有：轿车、小货、大货驾驶员培训、大货增考大客驾驶员培训、陪驾服务、营运驾驶员从业资格证书培训和交通运输相关工种的初、中、高级的技术等级培训。学校师资力量雄厚，配套设施齐全。拥有80余名专业从教人员，其中具有中高级职称、二、三级实习指导教师资格和高技术等级的占85;拥有大客、大货、吉普、桑塔纳等各类教练车100余辆;拥有可用教学场地多达2万平方米，此外还拥有车辆修理等后勤服务设施。    长运驾校培训管理科学，办学经验丰富，提倡理论和实践相结合，注重学员驾驶实际技能的培养，形成了富有自身特色的教学体系。为确保培训质量，学校对各部门以合同形式下达教学、安全等管理指标，并对各岗位职责实行量化考核。为使培训工作规范有效，学校制定了包括教练员&#8220;十不准&#8221;和&#8220;十大服务承诺&#8221;等一系列管理细则。";s:8:"allcount";s:2:"85";s:11:"passedcount";s:1:"0";s:11:"praisecount";s:1:"0";s:5:"price";s:4:"4200";s:7:"hotflag";s:1:"0";s:13:"recommendflag";s:1:"0";s:15:"certificateflag";s:1:"0";s:8:"pickflag";s:1:"0";s:8:"timeflag";s:1:"0";s:12:"trainaddress";s:0:"";s:6:"verify";s:1:"3";}
