//000000000300a:21:{s:2:"id";s:2:"27";s:6:"userid";s:36:"10936A48_B36D_666E_F50A_BB77CAFCA042";s:8:"nickname";s:12:"大众驾校";s:3:"img";s:40:"10936A48_B36D_666E_F50A_BB77CAFCA042.png";s:8:"piccount";s:1:"1";s:5:"phone";s:12:"400-8040-517";s:7:"address";s:0:"";s:9:"pickrange";s:0:"";s:5:"score";s:3:"4.7";s:12:"introduction";s:1795:"上海大众交通机动车驾驶员培训中心（大众驾校）位于青浦区华新镇北青公路,占地面积近500亩,是大众交通（集团）股份有限公司下属专门从事驾驶员培训的单位。是集现代化、科学化、系统化于一体的大型全封闭机动车驾驶员培训基地，现有近千辆培训车辆进场训练考试。大众驾校场地布局科学合理、规模宏大，基地实施均按未来高速发展的现代化城市的模拟道路，设置各个训练考试科目；拥有交通法规无纸考试系统；红外线桩考仪和路考仪考试系统，配备了专业的经营管理人员及齐全的服务配套设施。      大众驾校以“风正务实、温馨培训”为办学方针，确立“一切服务以学员为中心”的服务理念，倡导“善待学员、快乐学车”的服务宗旨，率先尝试全新的“有情教学关系、个性化教学模式”；本公司还向社会推出“明明白白消费，轻轻松松学车；收您一条烟，还您培训费”的承诺，教练员在带教中信守企业的承诺，“视质量为生命，对学员似亲人”，做到廉洁自律，保证质量，为社会输送合格的驾驶员。欢迎社会各界人士来大众驾校参加培训。        大众驾校具有三个训练场地：青浦大众基地、嘉定龙泉基地 、宝山刘行基地，配备经验丰富的教练员，教练员全部具备教练员资格证书，科学设计教学计划，通过率高。        大众驾校还是市出租汽车管理处、市陆上运输管理处授权进行出租汽车、货运出租《准营证》培训和复训带教的办学单位，为有志于从事客运、货运出租工作的人士提供配套培训，为再就业者提供良好的择业条件。";s:8:"allcount";s:4:"1269";s:11:"passedcount";s:4:"1250";s:11:"praisecount";s:1:"5";s:5:"price";s:4:"9885";s:7:"hotflag";s:1:"0";s:13:"recommendflag";s:1:"0";s:15:"certificateflag";s:1:"0";s:8:"pickflag";s:1:"0";s:8:"timeflag";s:1:"0";s:12:"trainaddress";s:0:"";s:6:"verify";s:1:"3";}
